import TextField from '@mui/material/TextField';
import { Button, Typography } from '@mui/material';
import TaskIcon from '@mui/icons-material/Task';
import { useNavigate, Link } from 'react-router-dom';
import React, { useState, useEffect } from "react";
import { API_URL } from "../utils";

export const LoginBody = () => {
  return(
    <div className="login-body"></div>
  )

}
// Define the LoginForm component
export const LoginForm = () => {
  const [newUser, setNewUser] = useState(""); // Organization ID
  const [newEmail, setNewEmail] = useState(""); // Email address
  const [userNum, setuserNum] = useState(""); // Index of the user in the allUsers array
  const [allUsers, setAllUsers] = useState([]); // Array containing all users
  const [currentUser, setCurrentUser] = useState({}); // Current user object
  const [UserID, setUserID] = useState("")
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const navigate = useNavigate();

  // Function to handle form submission
  const findUser = async () => {
    try {
      console.log(UserID)
      const response = await fetch(`${API_URL}/user?UserID=${(UserID)}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': 'none'
        },
        body: null,
      });
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
    
      const result = await response.json();
      console.log("response", response)
      
      console.log("result", `${API_URL}/user?UserID=${parseInt(UserID)}`);
      setCurrentUser({
        UserID: result.user.UserID,
        OrgID: result.user.OrgID,
        firstName: result.user.firstName,
        lastName: result.user.lastName,
        email: result.user.email
      });
      setIsLoggedIn(true);

      
      
      // Handle the response data here
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    console.log(currentUser);
    if (Object.keys(currentUser).length > 0) {
      navigate('/', { state: currentUser });
    }
  }, [isLoggedIn, currentUser]);

  //Page Body
  return (
    <LoginBody>
      <div className='rectangle'>
        <div className="logo-container">
          <img src="thatpivc.png" alt="WebWiz Logo" className="logo"/>
          <span  className="logo-text">WEBWIZ</span>
        </div>
        <Typography align="left" variant="h6" paddingTop={2} paddingBottom={2}>
          Login to your WebWiz account
        </Typography>
        <div className='addUserForm'>
          <TextField fullWidth size='small'
            id="email"
            label="Email"
            variant="outlined"
            value={newEmail}
            onChange={(e) => setNewEmail(e.target.value)}
            name='email'/>
        </div>
        <div className='addUserForm'>
          <TextField fullWidth size='small'
            id="outlined-basic"
            label="User ID"
            variant="outlined"
            value={UserID}
            onChange={(e) => setUserID(e.target.value)}
            name='UserID'/>
        </div>
        <Button size='big' disabled={!UserID.length || !newEmail.length}
          variant='outlined' onClick={findUser} sx={{paddingTop: "2"}}>
          <TaskIcon/>Login
        </Button>
        <div className='tinytext'>
          By logging into your WebWiz account, you accept all of the WebWiz terms and conditions
        </div>
        <div className='tinytext'>
          Dont have an account?! Create one <Link to='/create-account'>here!</Link>
        </div>
      </div>
    </LoginBody>
  );
}
