import TextField from '@mui/material/TextField';
import { Button, Typography } from '@mui/material';
import TaskIcon from '@mui/icons-material/Task';
import { useNavigate, useLocation } from 'react-router-dom';
import React, { useState, useEffect } from "react";
import axios from "axios";
import { API_URL } from "../utils";

// Define the LoginForm component
export const DeleteUserForm = () => {
    const location = useLocation();
    const { state } = location;
    const [currentUser, setCurrentUser] = useState(state);
    console.log(currentUser)
    const [tUserID, setTUserID] = useState("")
    const [allUsers, setAllUsers] = useState({})
    const [newUserID, setnewUserID] = useState("")

    const navigate = useNavigate();
    useEffect(() => {
      if (currentUser == null) {
        console.log(currentUser);
        navigate('/login');
      }
    }, [currentUser]);
    
  

  // Function to handle form submission
  const DeleteUser = async () => {
    try {
      const response = await fetch(`${API_URL}/user`, {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': 'none'
        },
        body: JSON.stringify({
          UserID: tUserID,
        }),
        mode: 'cors'
      });
      setCurrentUser({
        UserID: null,
        OrgID: null,
        firstName: null,
        lastName: null,
        Email: null
      })
  
      const result = await response.json();
      console.log(result);
      console.log(JSON.stringify(currentUser));
      navigate('/login');
      // Handle the response data here
    } catch (error) {
      console.error(error);
    }
  };
    
  return (
    <loginBody>
      <div className='rectangle'>
        <div className="logo-container">
          <img src="thatpivc.png" alt="WebWiz Logo" className="logo"/>
          <span className="logo-text">WEBWIZ</span>
        </div>
        <Typography align="left" variant="h6" paddingTop={2} paddingBottom={2}>
          {currentUser?.firstName}, we are sad to see you go. We're sorry to see you go and would love to hear if there's anything we can do to make you stay. If you’re sure about your decision, we’ll respect it and proceed. We wil forever long having you as a part of the WebWiz™ community, But if there’s something we can improve, please let us know. Thank you for being a valuable part of WebWiz™ and we hope to see you again very soon!
        </Typography>
          <TextField fullWidth size='small'
            id="outlined-basic"
            label="User ID"
            variant="outlined"
            value={tUserID}
            onChange={(e) => setTUserID(e.target.value)}
            name='OrgID'/>
        <Button size='big' disabled={!tUserID}
          variant='outlined' onClick={DeleteUser} paddingTop={2}>
          <TaskIcon/>Delete
        </Button>
        <div className='tinytext'>
          By deleting your WebWiz account, you have aknowledged that this is a permanant change and you will no longer be a part of WebWiz™.
        </div>
      </div>
    </loginBody>
  );
}
